import React, { useEffect, useState } from 'react'
import Header from '../components/Header'
import { AppContext } from '../context/context'
import axios from 'axios'

const Alert = ({ history }) => {
  const { loginUser, fetchData } = React.useContext(AppContext)
  const [virus, setVirus] = useState('')
  const [location, setLocation] = useState('')

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('loginUser'))
    !user && history.push('/')
  }, [history, loginUser])

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (loginUser.name && location && virus) {
      try {
        await axios.post('/api/alert', {
          user: loginUser.name,
          location,
          virus
        })
        history.push('/home')
      } catch (e) {
        console.log('Cannot Create Post')
      }
    }
  }

  return (
    <>
      <Header />
      <div className='container'>
        <h2 className='text-center my-5'>Alert</h2>
        <form
          className='col-10 col-lg-6 m-auto border p-5 rounded'
          onSubmit={handleSubmit}
        >
          <div className='form-group'>
            <label htmlFor='location'>Location</label>
            <select
              className='form-control rounded'
              id='location'
              onChange={(e) => setLocation(e.target.value)}
              value={location}
            >
              <option>University City Campus</option>
              <option>Center City Campus</option>
              <option>Queen Lane Campus</option>
              <option>Natural Sciences</option>
            </select>
          </div>
          <button className='btn btn-primary btn-block mb-3'>Submit</button>
        </form>
      </div>
    </>
  )
}

export default Alert
